void printOrder(Order_t *Order) {
	
	system("cls");
	
	
	
	int i = 1;
	
	Node_t *ResourceList;
	Resource_t *tmpResource;
	
	printf(ANSI_COLOR_BRIGHTRED " [Ordine selezionato]\n");
	
	printf(ANSI_COLOR_BRIGHTRED" +----------------------------------------------+\n");
	printf(" |   No. |   ID Isola   |      Richiedente      |\n");
	printf(" +----------------------------------------------+\n"ANSI_COLOR_BRIGHTYELLOW);
	
	printf(ANSI_COLOR_BRIGHTRED " | " ANSI_COLOR_BRIGHTYELLOW "%4d. " ANSI_COLOR_BRIGHTRED "|" ANSI_COLOR_BRIGHTYELLOW " %12u "ANSI_COLOR_BRIGHTRED "|" ANSI_COLOR_BRIGHTYELLOW " %21s "ANSI_COLOR_BRIGHTRED"|\n", i, Order->IsleID, Order->CF);
	
	printf(" +----------------------------------------------+\n");
	printf(" |    "ANSI_COLOR_BRIGHTYELLOW"|"ANSI_COLOR_BRIGHTRED"  |   Quantita'  |        Risorsa        |\n");
	printf(" +----------------------------------------------+\n"ANSI_COLOR_BRIGHTYELLOW);
	
	ResourceList = Order->Resources;
	
	while(ResourceList != NULL) {
		tmpResource = ResourceList->Data;
		printf(ANSI_COLOR_BRIGHTRED " | "ANSI_COLOR_BRIGHTYELLOW "   +"ANSI_COLOR_BRIGHTRED"-"ANSI_COLOR_BRIGHTYELLOW">"ANSI_COLOR_BRIGHTRED"|"ANSI_COLOR_BRIGHTYELLOW" %12d "ANSI_COLOR_BRIGHTRED"|"ANSI_COLOR_BRIGHTYELLOW" %21s "ANSI_COLOR_BRIGHTRED"|\n", tmpResource->Quantity, tmpResource->Name);
		ResourceList = ResourceList->next;
	}
	
	printf(ANSI_COLOR_BRIGHTRED" +----------------------------------------------+\n\n" ANSI_COLOR_BRIGHTYELLOW);
	
	return;
}

int getVerticalInput(Choice_t ChoiceList[], int TotalChoices, void (*printOptions)(Choice_t[], int)) {
	
	char key_code;
	
	int option = 0;
	int i = 0;
	
	ChoiceList[0].Selected = true;
	
	printOptions(ChoiceList, TotalChoices);
	
	while(1) {
		
		if(_kbhit()) {
			
			key_code = _getch();
			
			int tmp;
			
			if(key_code == 72) {
				tmp = option;
				tmp--;
				if(tmp >= 0) {
					ChoiceList[option].Selected = false;
					option--;
					ChoiceList[option].Selected = true;
					printOptions(ChoiceList, TotalChoices);
				}
			} else if (key_code == 80){
				tmp = option;
				tmp++;
				if(tmp < TotalChoices) {
					ChoiceList[option].Selected = false;
					option++;
					ChoiceList[option].Selected = true;
					printOptions(ChoiceList, TotalChoices);
				}
			} else if(key_code == 13) {
				
				for(i = 0; i < TotalChoices; i++) {
					ChoiceList[i].Selected = false;
				}
				
				return option;
				
			}
		}
		
	}
	
}

